"use strict";if(window.localStorage.userArr)var array=JSON.parse(window.localStorage.userArr);else array=[];$("#btn").bind("click",function(){for(var a=$("#txt").val(),r=$("#pass").val(),e=!1,l=0,o=0;o<array.length;o++)a==array[o].username&&(e=!0,l=o);if(e)if(r==array[l].password){alert("登录成功");var t=$("txt");localStorage.name=t.val(),console.log(localStorage.name),window.location.href="../src/index.html"}else alert("密码错误");else alert("用户不存在，请先注册")});