"use strict";if(window.localStorage.userArr)var array=JSON.parse(window.localStorage.userArr);else array=[];$("#btn").bind("click",function(){for(var r=$("#user").val(),a=$("#pass").val(),e=0;e<array.length;e++)if(r==array[e].username)return alert("该账号已存在"),void(window.location.href="../src/zhuce.html");var o={username:r,password:a};array.push(o),window.localStorage.userArr=JSON.stringify(array),alert("用户创建成功"),window.location.href="../src/denglu.html"});